-- auto-generated definitioncreate table tbl_file(    id        int auto_increment        primary key,    file_sha1 char(40)      default ''                not null comment '文件hash',    file_name varchar(256)  default ''                not null comment '文件名',    file_size bigint        default 0                 null comment '文件大小',    file_addr varchar(1024) default ''                not null comment '文件存储配置',    create_at datetime      default CURRENT_TIMESTAMP null comment '创建日期',    update_at datetime      default CURRENT_TIMESTAMP null comment '更新日期',    status    int           default 0                 not null comment '状态（可用/禁用/已删除等状态）',    ext1      int           default 0                 not null comment '备用字段1',    ext2      text                                    null comment '备用字段2',    constraint tbl_file_file_sha1_uindex        unique (file_sha1));-- auto-generated definitioncreate table tbl_user(    id              int auto_increment        primary key,    user_name       varchar(64)  default ''                not null comment '用户名',    user_pwd        varchar(256) default ''                not null comment '用户encoded密码',    email           varchar(64)  default ''                null comment '邮箱',    phone           varchar(128) default ''                null comment '手机号',    email_validated tinyint      default 0                 null comment '邮箱是否已验证',    phone_validated tinyint      default 0                 null comment '手机号是否已验证',    signup_at       datetime     default CURRENT_TIMESTAMP null comment '注册日期',    last_active     datetime     default CURRENT_TIMESTAMP null on update CURRENT_TIMESTAMP comment '最后活跃时间戳',    profile         text                                   null comment '用户属性',    status          int          default 0                 not null comment '账户状态（启用/禁用/锁定/标记删除等）');CREATE TABLE `tbl_user_token` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `user_name` varchar(64) NOT NULL DEFAULT '' COMMENT '用户名',  `user_token` char(40) NOT NULL DEFAULT '' COMMENT '用户登录token',    PRIMARY KEY (`id`),  UNIQUE KEY `idx_username` (`user_name`));-- 创建用户文件表CREATE TABLE `tbl_user_file` (  `id` int(11) NOT NULL PRIMARY KEY AUTO_INCREMENT,  `user_name` varchar(64) NOT NULL,  `file_sha1` varchar(64) NOT NULL DEFAULT '' COMMENT '文件hash',  `file_size` bigint(20) DEFAULT '0' COMMENT '文件大小',  `file_name` varchar(256) NOT NULL DEFAULT '' COMMENT '文件名',  `upload_at` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '上传时间',  `last_update` datetime DEFAULT CURRENT_TIMESTAMP          ON UPDATE CURRENT_TIMESTAMP COMMENT '最后修改时间',  `status` int(11) NOT NULL DEFAULT '0' COMMENT '文件状态(0正常1已删除2禁用)',  UNIQUE KEY `idx_user_file` (`user_name`, `file_sha1`),  KEY `idx_status` (`status`),  KEY `idx_user_id` (`user_name`));